import{_ as s,c as d,a,w as r,e as i,r as t,o as h,b as e,d as o}from"./404.md.4e630b9f.js";const C='{"title":"Software and preparation","description":"","frontmatter":{"title":"Software and preparation","category":"Guide","nav_order":3,"prefix":"3. "},"headers":[{"level":2,"title":"Download Minecraft Bedrock Edition","slug":"download-minecraft-bedrock-edition"},{"level":2,"title":"Picking an Editor","slug":"picking-an-editor"},{"level":3,"title":"VSCode","slug":"vscode"},{"level":3,"title":"bridge.","slug":"bridge"},{"level":3,"title":"Mobile Editors","slug":"mobile-editors"},{"level":2,"title":"Blockbench","slug":"blockbench"},{"level":2,"title":"Image Editors","slug":"image-editors"},{"level":3,"title":"Krita","slug":"krita"},{"level":3,"title":"GIMP","slug":"gimp"},{"level":3,"title":"Paint\u2024net","slug":"paint\u2024net"},{"level":3,"title":"Pixilart","slug":"pixilart"},{"level":3,"title":"Piskel","slug":"piskel"},{"level":2,"title":"Additional Materials","slug":"additional-materials"},{"level":3,"title":"Join the Discord","slug":"join-the-discord"},{"level":3,"title":"Vanilla Packs","slug":"vanilla-packs"},{"level":3,"title":"Documentation","slug":"documentation"},{"level":3,"title":"Troubleshooting and Additional Help","slug":"troubleshooting-and-additional-help"},{"level":3,"title":"Additional Links and Tools","slug":"additional-links-and-tools"},{"level":2,"title":"Your progress so far","slug":"your-progress-so-far"}],"relativePath":"guide/software-preparation.md"}',c={},p=i('<p>Before you can start creating addons, you first have to install the required tools and applications. While development will be easiest on Windows 10, we have provided mobile alternatives for both Android and iOS, where applicable.</p><h2 id="download-minecraft-bedrock-edition" tabindex="-1">Download Minecraft Bedrock Edition <a class="header-anchor" href="#download-minecraft-bedrock-edition" aria-hidden="true">#</a></h2><ul><li><a href="https://www.microsoft.com/en-us/p/minecraft-for-windows-10/9nblggh2jhxj?activetab=pivot:overviewtab" target="_blank" rel="noopener noreferrer">Windows 10</a></li><li><a href="https://play.google.com/store/apps/details?id=com.mojang.minecraftpe&amp;hl=en" target="_blank" rel="noopener noreferrer">Android</a></li><li><a href="https://apps.apple.com/us/app/minecraft/id479516143" target="_blank" rel="noopener noreferrer">iOS</a></li><li><a href="https://discord.gg/VJTZ3KaTx6" target="_blank" rel="noopener noreferrer">Run MC on Linux</a></li></ul><h2 id="picking-an-editor" tabindex="-1">Picking an Editor <a class="header-anchor" href="#picking-an-editor" aria-hidden="true">#</a></h2><p>Addons can be created using any text editor, however it&#39;s much more comfortable to work in a dedicated editor. A good editor can give you code-completion, error-detection, and in-editor documentation.</p><p>There are strong opinions about the best editor for beginners, but generally speaking you cannot go wrong selecting either VSCode, or bridge. If you are mobile, you will need to use a mobile alternative.</p><h3 id="vscode" tabindex="-1">VSCode <a class="header-anchor" href="#vscode" aria-hidden="true">#</a></h3><p>VSCode is a general purpose text-editor and IDE. With VSCode, you will be able to edit your addons in plain-text, guided along by a powerful array of extensions and addons. VSCode is a great option for programmers and advanced users.</p><p><a href="https://code.visualstudio.com/" target="_blank" rel="noopener noreferrer">\u2699\uFE0FInstall VSCode</a></p>',9),u=e("p",null,"Many packages exist for VSCode that make editing addons easier:",-1),f=e("ul",null,[e("li",null,[e("a",{href:"https://marketplace.visualstudio.com/items?itemName=BlockceptionLtd.blockceptionvscodeminecraftbedrockdevelopmentextension",target:"_blank",rel:"noopener noreferrer"},"Blockceptions Minecraft Bedrock Development")]),e("li",null,[e("a",{href:"https://marketplace.visualstudio.com/items?itemName=arcensoth.language-mcfunction",target:"_blank",rel:"noopener noreferrer"},".mcfunction support")]),e("li",null,[e("a",{href:"https://marketplace.visualstudio.com/items?itemName=zz5840.minecraft-lang-colorizer",target:"_blank",rel:"noopener noreferrer"},".lang support")]),e("li",null,[e("a",{href:"https://marketplace.visualstudio.com/items?itemName=destruc7i0n.vscode-bedrock-definitions",target:"_blank",rel:"noopener noreferrer"},"Bedrock Definitions")]),e("li",null,[e("a",{href:"https://marketplace.visualstudio.com/items?itemName=mohsen1.prettify-json",target:"_blank",rel:"noopener noreferrer"},"Prettify-json")]),e("li",null,[e("a",{href:"https://marketplace.visualstudio.com/items?itemName=streetsidesoftware.code-spell-checker",target:"_blank",rel:"noopener noreferrer"},"Spell Checker (for writing wiki)")]),e("li",null,[e("a",{href:"https://marketplace.visualstudio.com/items?itemName=JannisX11.snowstorm",target:"_blank",rel:"noopener noreferrer"},"Snowstorm Particle Editor")]),e("li",null,[e("a",{href:"https://marketplace.visualstudio.com/items?itemName=CoenraadS.bracket-pair-colorizer-2",target:"_blank",rel:"noopener noreferrer"},"Bracket Pair Colorizer")]),e("li",null,[e("a",{href:"https://marketplace.visualstudio.com/items?itemName=netcorext.uuid-generator",target:"_blank",rel:"noopener noreferrer"},"UUID Generator")])],-1),m=i('<h3 id="bridge" tabindex="-1">bridge. <a class="header-anchor" href="#bridge" aria-hidden="true">#</a></h3><p><a href="https://bridge-core.app/" target="_blank" rel="noopener noreferrer">bridge.</a> is a light-weight, dedicated IDE for Minecraft addons. It features <a href="https://bridge-core.app/guide/features/" target="_blank" rel="noopener noreferrer">innovative features</a> such as <a href="https://bridge-core.app/guide/features/index.html#file-previews" target="_blank" rel="noopener noreferrer">entity and block previews</a>, <a href="https://bridge-core.app/guide/features/index.html#auto-completions-and-validation" target="_blank" rel="noopener noreferrer">rich auto-completions and file validations</a> and <a href="https://bridge-core.app/guide/features/index.html#advanced-file-creation" target="_blank" rel="noopener noreferrer">advanced file creation with presets</a>. bridge. includes a regular text editor for experienced addon creators and a tree editor to make it easy for beginners to get started with editing JSON files.</p><h4 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-hidden="true">#</a></h4><ul><li><a href="https://bridge-core.app/guide/why-bridge.html" target="_blank" rel="noopener noreferrer">Read more on why you should use bridge.</a></li><li><a href="https://bridge-core.app/guide/index.html" target="_blank" rel="noopener noreferrer">Read our guide on getting started with bridge.</a></li><li><a href="https://editor.bridge-core.app/" target="_blank" rel="noopener noreferrer">Try out bridge. online</a></li></ul><h3 id="mobile-editors" tabindex="-1">Mobile Editors <a class="header-anchor" href="#mobile-editors" aria-hidden="true">#</a></h3><h4 id="android" tabindex="-1">Android <a class="header-anchor" href="#android" aria-hidden="true">#</a></h4><ul><li><a href="https://play.google.com/store/apps/details?id=com.foxdebug.acodefree" target="_blank" rel="noopener noreferrer">ACode Editor</a></li><li><a href="https://bridge-core.app/" target="_blank" rel="noopener noreferrer">bridge. v2</a></li></ul><h4 id="ios" tabindex="-1">iOS <a class="header-anchor" href="#ios" aria-hidden="true">#</a></h4><ul><li><a href="https://apps.apple.com/us/app/kodex/id1038574481" target="_blank" rel="noopener noreferrer">Kodex</a></li><li><a href="https://bridge-core.app/" target="_blank" rel="noopener noreferrer">bridge. v2</a></li></ul><h2 id="blockbench" tabindex="-1">Blockbench <a class="header-anchor" href="#blockbench" aria-hidden="true">#</a></h2><ul><li><a href="https://blockbench.net/" target="_blank" rel="noopener noreferrer">Blockbench</a> is a &#39;boxy 3D model editor&#39; typically used to create Minecraft models, textures and animations. Also provides a web-browser version which is compatible with mobile.</li></ul><h2 id="image-editors" tabindex="-1">Image Editors <a class="header-anchor" href="#image-editors" aria-hidden="true">#</a></h2><p>When choosing an image editor, it is important to keep in mind that the traditional Minecraft style is composed of simple 16X16 pixelart. There are plenty of powerful and free art programs available for you to use. However, many of these programs have more tools than you will need for Minecraft graphical design and these tools require time to learn.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Choose a program that feels comfortable and easy for you to use. Many Addon creators use different art programs for different tasks. (Example: One might use paint\u2024net for most of the art, and piskel for Minecraft block animations). Choose what works best for you!</p></div><h3 id="krita" tabindex="-1">Krita <a class="header-anchor" href="#krita" aria-hidden="true">#</a></h3><p>Krita is a powerful open-source art programed with the goal of giving free powerful digital art tools to artists. Krita has more than enough features to cover your Minecraft needs and works on a MAC or PC. Pros: Plenty of features including a pixel brush with an intuitive user interface. Cons: Requires a little time to become familar with the tools.</p><p><a href="https://krita.org/en/" target="_blank" rel="noopener noreferrer">Download Krita</a></p><h3 id="gimp" tabindex="-1">GIMP <a class="header-anchor" href="#gimp" aria-hidden="true">#</a></h3><p>Gimp is similar to Krita in that it is a free and open source digital art program that has a vast arrays of tools. Where Krita focuses more on illustration, GIMP focuses more on image manipulation (think Photoshop). Gimp also works on MAC or PC. Pros: GIMP has more than enough tools for editing Minecraft art Cons: The interface is not intuitive. Even though GIMP is powerful, it requires a steep learning curve.</p><p><a href="https://www.gimp.org/" target="_blank" rel="noopener noreferrer">Download Gimp</a></p><h3 id="paint\u2024net" tabindex="-1">Paint\u2024net <a class="header-anchor" href="#paint\u2024net" aria-hidden="true">#</a></h3><p>Paint\u2024net is a simple yet powerful image editing and art software. Paint\u2024net may not have a vast array of tools like Krita and GIMP, but it does offer simplicity and ease of use. Pros: Easy to use and learn. Cons: Only works on Windows.</p><p><a href="https://www.getpaint.net" target="_blank" rel="noopener noreferrer">Download Paint.net</a></p><h3 id="pixilart" tabindex="-1">Pixilart <a class="header-anchor" href="#pixilart" aria-hidden="true">#</a></h3><p>Pixilart is a web-based pixel art software. It is extremely simple to use since it is focused on pixel art. It also has a powerful resize option that may come in handy, so that you can resize your art without losing the pixelart details.</p><p>Pros: Easy to use and learn. Curated specifically for pixel art. Cons: Must have internet connection. May be missing tools you want.</p><p><a href="https://www.pixilart.com/" target="_blank" rel="noopener noreferrer">Use Pixilart</a></p><h3 id="piskel" tabindex="-1">Piskel <a class="header-anchor" href="#piskel" aria-hidden="true">#</a></h3><p>Piskel is a web-based pixel art software with a focus of making pixelated sprites (or video game charater animations). This tool, similar to Pixilart, is simple to use. This is also a great tool for making flipbooks (Minecraft block or skin animations).</p><p>Pros: Easy to use and learn. Perfect for flipbook animations Cons: Must have internet connection. Only offers the most basic tools.</p><p><a href="https://www.piskelapp.com/" target="_blank" rel="noopener noreferrer">Download Piskel</a></p><h2 id="additional-materials" tabindex="-1">Additional Materials <a class="header-anchor" href="#additional-materials" aria-hidden="true">#</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>This guide will walk you through the first stages of addon development, but it is not comprehensive! To learn more about addons, you will have to use and reference other sources of information, which we will link to here.</p></div><h3 id="join-the-discord" tabindex="-1">Join the Discord <a class="header-anchor" href="#join-the-discord" aria-hidden="true">#</a></h3><p>The best place to get help with this guide is to join the <a href="/discord.html">discord server</a>.</p><h3 id="vanilla-packs" tabindex="-1">Vanilla Packs <a class="header-anchor" href="#vanilla-packs" aria-hidden="true">#</a></h3><p>Minecraft&#39;s vanilla files are a good source of reference material. You should download these packs, and store them on a convenient location on your computer. When you need an example of an item, or entity, or animation, you can reference these files for inspiration.</p><ul><li><a href="https://aka.ms/resourcepacktemplate" target="_blank" rel="noopener noreferrer">Vanilla Resource Pack</a></li><li><a href="https://aka.ms/behaviorpacktemplate" target="_blank" rel="noopener noreferrer">Vanilla Behavior Pack</a></li></ul><h3 id="documentation" tabindex="-1">Documentation <a class="header-anchor" href="#documentation" aria-hidden="true">#</a></h3><p>There are many good sources of Addon documentation. Familiarize yourself with all of them, and consider bookmarking them.</p><ul><li><a href="https://bedrock.dev/" target="_blank" rel="noopener noreferrer">bedrock.dev</a>: Reference documentation.</li><li><a href="https://wiki.bedrock.dev/" target="_blank" rel="noopener noreferrer">wiki.bedrock.dev</a>: Tutorials and guides.</li><li><a href="https://docs.microsoft.com/en-us/minecraft/creator/" target="_blank" rel="noopener noreferrer">MS Docs</a>: The official microsoft creator portal for addons.</li></ul><h3 id="troubleshooting-and-additional-help" tabindex="-1">Troubleshooting and Additional Help <a class="header-anchor" href="#troubleshooting-and-additional-help" aria-hidden="true">#</a></h3><ul><li>If the json format is very tricky for you, consider reading the <a href="/guide/understanding-json.html">understanding-json guide</a>.</li><li>If you get stuck with an odd error, consider reading the <a href="/guide/troubleshooting.html">troubleshooting guide</a>.</li><li>If you still haven&#39;t found a solution, feel free to join our <a href="/discord.html">Discord server</a>.</li></ul><h3 id="additional-links-and-tools" tabindex="-1">Additional Links and Tools <a class="header-anchor" href="#additional-links-and-tools" aria-hidden="true">#</a></h3><ul><li>You can explore additional tools <a href="/meta/useful-links.html">here</a>.</li></ul><h2 id="your-progress-so-far" tabindex="-1">Your progress so far <a class="header-anchor" href="#your-progress-so-far" aria-hidden="true">#</a></h2>',46),g=e("ul",null,[e("li",null,"[x] Installed the necessary software"),e("li",null,"[x] Downloaded the Vanilla Example files"),e("li",null,[o("[ ] Locate your "),e("code",null,"com.mojang"),o(" folder and create your addon's workspace.")]),e("li",null,"[ ] Create the manifest and pack icon for your first addon")],-1);function b(k,v,w,_,x,y){const n=t("Spoiler"),l=t("Checklist");return h(),d("div",null,[p,a(n,{title:"Configuring VSCode"},{default:r(()=>[u,f]),_:1}),m,a(l,null,{default:r(()=>[g]),_:1})])}var T=s(c,[["render",b]]);export{C as __pageData,T as default};
