import{_ as t,c as r,o as e,e as a}from"./404.md.286bf167.js";const h=JSON.parse('{"title":"Vanilla Particles","description":"","frontmatter":{"title":"Vanilla Particles","category":"Documentation"},"headers":[{"level":3,"title":"Working Particles","slug":"working-particles","link":"#working-particles","children":[]},{"level":3,"title":"Particles with Issues","slug":"particles-with-issues","link":"#particles-with-issues","children":[]},{"level":3,"title":"Bubble Particles","slug":"bubble-particles","link":"#bubble-particles","children":[]},{"level":3,"title":"Permanent Particles","slug":"permanent-particles","link":"#permanent-particles","children":[]},{"level":2,"title":"Broken Particles","slug":"broken-particles","link":"#broken-particles","children":[]},{"level":2,"title":"Component Particles","slug":"component-particles","link":"#component-particles","children":[]}],"relativePath":"particles/vanilla-particles.md"}'),d={name:"particles/vanilla-particles.md"},i=a('<p>Here is the complete list of Bedrock particles from the vanilla resources. Please be aware that not all of these particles function properly, as many require molang context from their host entity.</p><div class="tip custom-block"><p class="custom-block-title">Playing via /particle:</p><p>For some reason, Bedrock requires the leading <code>minecraft</code> namespace, and the coordinates in the <code>/particle</code> command.</p><p>It doesn&#39;t have autocomplete for particles.</p></div><h3 id="working-particles" tabindex="-1">Working Particles <a class="header-anchor" href="#working-particles" aria-hidden="true">#</a></h3><p>These particles can be spawned directly in-world, without any issues.</p><table><thead><tr><th>Working Particles</th></tr></thead><tbody><tr><td>minecraft:basic_flame_particle</td></tr><tr><td>minecraft:basic_portal_particle</td></tr><tr><td>minecraft:basic_smoke_particle</td></tr><tr><td>minecraft:bleach</td></tr><tr><td>minecraft:blue_flame_particle</td></tr><tr><td>minecraft:camera_shoot_explosion</td></tr><tr><td>minecraft:campfire_smoke_particle</td></tr><tr><td>minecraft:campfire_tall_smoke_particle</td></tr><tr><td>minecraft:candle_flame_particle</td></tr><tr><td>minecraft:critical_hit_emitter</td></tr><tr><td>minecraft:crop_growth_emitter</td></tr><tr><td>minecraft:dragon_breath_trail</td></tr><tr><td>minecraft:dragon_death_explosion_emitter</td></tr><tr><td>minecraft:dragon_destroy_block</td></tr><tr><td>minecraft:dragon_dying_explosion</td></tr><tr><td>minecraft:endrod</td></tr><tr><td>minecraft:end_chest</td></tr><tr><td>minecraft:evocation_fang_particle</td></tr><tr><td>minecraft:evoker_spell</td></tr><tr><td>minecraft:cauldron_explosion_emitter</td></tr><tr><td>minecraft:egg_destroy_emitter</td></tr><tr><td>minecraft:falling_border_dust_particle</td></tr><tr><td>minecraft:falling_dust_dragon_egg_particle</td></tr><tr><td>minecraft:falling_dust_gravel_particle</td></tr><tr><td>minecraft:falling_dust_red_sand_particle</td></tr><tr><td>minecraft:falling_dust_sand_particle</td></tr><tr><td>minecraft:falling_dust_scaffolding_particle</td></tr><tr><td>minecraft:falling_dust_top_snow_particle</td></tr><tr><td>minecraft:heart_particle</td></tr><tr><td>minecraft:honey_drip_particle</td></tr><tr><td>minecraft:huge_explosion_lab_misc_emitter</td></tr><tr><td>minecraft:huge_explosion_emitter</td></tr><tr><td>minecraft:ice_evaporation_emitter</td></tr><tr><td>minecraft:knockback_roar_particle</td></tr><tr><td>minecraft:lab_table_misc_mystical_particle</td></tr><tr><td>minecraft:large_explosion</td></tr><tr><td>minecraft:lava_drip_particle</td></tr><tr><td>minecraft:lava_particle</td></tr><tr><td>minecraft:llama_spit_smoke</td></tr><tr><td>minecraft:magnesium_salts_emitter</td></tr><tr><td>minecraft:mob_portal</td></tr><tr><td>minecraft:mycelium_dust_particle</td></tr><tr><td>minecraft:obsidian_glow_dust_particle</td></tr><tr><td>minecraft:obsidian_tear_particle</td></tr><tr><td>minecraft:redstone_ore_dust_particle</td></tr><tr><td>minecraft:redstone_repeater_dust_particle</td></tr><tr><td>minecraft:redstone_torch_dust_particle</td></tr><tr><td>minecraft:redstone_wire_dust_particle</td></tr><tr><td>minecraft:rising_border_dust_particle</td></tr><tr><td>minecraft:sculk_sensor_redstone_particle</td></tr><tr><td>minecraft:snowflake_particle</td></tr><tr><td>minecraft:spore_blossom_ambient_particle</td></tr><tr><td>minecraft:spore_blossom_shower_particle</td></tr><tr><td>minecraft:stalactite_lava_drip_particle</td></tr><tr><td>minecraft:stalactite_water_drip_particle</td></tr><tr><td>minecraft:totem_particle</td></tr><tr><td>minecraft:villager_angry</td></tr><tr><td>minecraft:villager_happy</td></tr><tr><td>minecraft:water_drip_particle</td></tr><tr><td>minecraft:water_evaporation_bucket_emitter</td></tr><tr><td>minecraft:water_splash_particle_manual</td></tr></tbody></table><h3 id="particles-with-issues" tabindex="-1">Particles with Issues <a class="header-anchor" href="#particles-with-issues" aria-hidden="true">#</a></h3><p>The following particles can be spawned, but might spam you with content log errors because they rely on variables that <code>/particle</code> cannot set:</p><table><thead><tr><th>Particles with issues</th></tr></thead><tbody><tr><td>minecraft:arrow_spell_emitter</td></tr><tr><td>minecraft:balloon_gas_particle</td></tr><tr><td>minecraft:basic_crit_particle</td></tr><tr><td>minecraft:conduit_absorb_particle</td></tr><tr><td>minecraft:conduit_attack_emitter</td></tr><tr><td>minecraft:dragon_breath_fire</td></tr><tr><td>minecraft:dragon_breath_lingering</td></tr><tr><td>minecraft:electric_spark_particle</td></tr><tr><td>minecraft:enchanting_table_particle</td></tr><tr><td>minecraft:elephant_tooth_paste_vapor_particle</td></tr><tr><td>minecraft:death_explosion_emitter</td></tr><tr><td>minecraft:eyeofender_death_explode_particle</td></tr><tr><td>minecraft:explosion_particle</td></tr><tr><td>minecraft:falling_dust_concrete_powder_particle</td></tr><tr><td>minecraft:lab_table_heatblock_dust_particle</td></tr><tr><td>minecraft:misc_fire_vapor_particle</td></tr><tr><td>minecraft:mobspell_emitter</td></tr><tr><td>minecraft:mob_block_spawn_emitter</td></tr><tr><td>minecraft:note_particle</td></tr><tr><td>minecraft:portal_directional</td></tr><tr><td>minecraft:portal_reverse_particle</td></tr><tr><td>minecraft:rain_splash_particle</td></tr><tr><td>minecraft:shulker_bullet</td></tr><tr><td>minecraft:silverfish_grief_emitter</td></tr><tr><td>minecraft:soul_particle</td></tr><tr><td>minecraft:sparkler_emitter</td></tr><tr><td>minecraft:splash_spell_emitter</td></tr><tr><td>minecraft:water_evaporation_actor_emitter</td></tr><tr><td>minecraft:water_splash_particle</td></tr><tr><td>minecraft:water_wake_particle</td></tr><tr><td>minecraft:wax_particle</td></tr><tr><td>minecraft:wither_boss_invulnerable</td></tr></tbody></table><h3 id="bubble-particles" tabindex="-1">Bubble Particles <a class="header-anchor" href="#bubble-particles" aria-hidden="true">#</a></h3><p>The following particles are various bubbles that only show up underwater. Some of them spam content log errors:</p><table><thead><tr><th>Bubble particles</th></tr></thead><tbody><tr><td>minecraft:basic_bubble_particle</td></tr><tr><td>minecraft:basic_bubble_particle_manual</td></tr><tr><td>minecraft:bubble_column_bubble</td></tr><tr><td>minecraft:bubble_column_down_particle</td></tr><tr><td>minecraft:bubble_column_up_particle</td></tr><tr><td>minecraft:cauldron_bubble_particle</td></tr><tr><td>minecraft:cauldron_splash_particle</td></tr><tr><td>minecraft:dolphin_move_particle</td></tr><tr><td>minecraft:eye_of_ender_bubble_particle</td></tr><tr><td>minecraft:fish_hook_particle</td></tr><tr><td>minecraft:fish_pos_particle</td></tr><tr><td>minecraft:glow_particle</td></tr><tr><td>minecraft:guardian_attack_particle</td></tr><tr><td>minecraft:guardian_water_move_particle</td></tr><tr><td>minecraft:sponge_absorb_water_particle</td></tr><tr><td>minecraft:squid_flee_particle</td></tr><tr><td>minecraft:squid_ink_bubble</td></tr><tr><td>minecraft:squid_move_particle</td></tr><tr><td>minecraft:underwater_torch_particle</td></tr></tbody></table><h3 id="permanent-particles" tabindex="-1">Permanent Particles <a class="header-anchor" href="#permanent-particles" aria-hidden="true">#</a></h3><p>The following particles are permanent and will not be removed once spawned until you exit the game:</p><table><thead><tr><th>Permanent particles</th></tr></thead><tbody><tr><td>minecraft:mobflame_emitter</td></tr><tr><td>minecraft:nectar_drip_particle</td></tr><tr><td>minecraft:phantom_trail_particle</td></tr><tr><td>minecraft:stunned_emitter</td></tr></tbody></table><h2 id="broken-particles" tabindex="-1">Broken Particles <a class="header-anchor" href="#broken-particles" aria-hidden="true">#</a></h2><p>The following particles exist in-game but cannot be spawned because they require context that cannot be provided by <code>/particle</code> or are simply bugged:</p><table><thead><tr><th>Broken particles</th></tr></thead><tbody><tr><td>minecraft:block_destruct</td></tr><tr><td>minecraft:block_slide</td></tr><tr><td>minecraft:breaking_item_icon</td></tr><tr><td>minecraft:breaking_item_terrain</td></tr><tr><td>minecraft:cauldron_spell_emitter</td></tr><tr><td>minecraft:ink_emitter</td></tr><tr><td>minecraft:portal_east_west</td></tr><tr><td>minecraft:portal_north_south</td></tr><tr><td>minecraft:vibration_signal</td></tr><tr><td>minecraft:colored_flame_particle</td></tr></tbody></table><hr><p><a href="https://www.reddit.com/r/MinecraftCommands/comments/cbd56p/i_need_a_list_of_bedrock_particles/etg8rt7/" target="_blank" rel="noreferrer">Original Credit</a></p><h2 id="component-particles" tabindex="-1">Component Particles <a class="header-anchor" href="#component-particles" aria-hidden="true">#</a></h2><p>The following is a list of pre-defined short names for Vanilla particles that can be used in certain components. <strong>These have all been proven to generally work. This may not be a full list.</strong></p><table><thead><tr><th>Short names</th><th>Notes</th></tr></thead><tbody><tr><td>mobspellambient</td><td>Color determined by any present potion ID in the component</td></tr><tr><td>villagerangry</td><td></td></tr><tr><td>bubble</td><td>Only shows underwater</td></tr><tr><td>evaporation</td><td></td></tr><tr><td>crit</td><td></td></tr><tr><td>dragonbreath</td><td>Only seems to work for the AoE component for Projectiles</td></tr><tr><td>driplava</td><td></td></tr><tr><td>dripwater</td><td></td></tr><tr><td>reddust</td><td></td></tr><tr><td>enchantingtable</td><td></td></tr><tr><td>endrod</td><td></td></tr><tr><td>mobspell</td><td>Color determined by any present potion ID in the component</td></tr><tr><td>largeexplode</td><td></td></tr><tr><td>hugeexplosion</td><td></td></tr><tr><td>fallingdust</td><td>Color determined by any present potion ID in the component</td></tr><tr><td>waterwake</td><td></td></tr><tr><td>flame</td><td></td></tr><tr><td>villagerhappy</td><td></td></tr><tr><td>heart</td><td></td></tr><tr><td>mobspellinstantaneous</td><td>Color determined by any present potion ID in the component</td></tr><tr><td>iconcrack</td><td></td></tr><tr><td>slime</td><td></td></tr><tr><td>snowballpoof</td><td></td></tr><tr><td>largesmoke</td><td></td></tr><tr><td>lava</td><td></td></tr><tr><td>mobflame</td><td></td></tr><tr><td>townaura</td><td></td></tr><tr><td>note</td><td></td></tr><tr><td>explode</td><td></td></tr><tr><td>portal</td><td></td></tr><tr><td>rainsplash</td><td></td></tr><tr><td>smoke</td><td></td></tr><tr><td>watersplash</td><td></td></tr><tr><td>ink</td><td></td></tr><tr><td>terrain</td><td>Pulls texture from atlas.terrain</td></tr><tr><td>totem</td><td></td></tr><tr><td>witchspell</td><td></td></tr><tr><td>soul</td><td></td></tr><tr><td>spit</td><td></td></tr><tr><td>sneeze</td><td></td></tr></tbody></table>',22),n=[i];function l(c,o,s,_,p,m){return e(),r("div",null,n)}const b=t(d,[["render",l]]);export{h as __pageData,b as default};
